let min=1,max=10,winningNum=getRandomNum(min,max),guessesLeft=3;const game=document.getElementById('game'),minNum=document.querySelector('.min-num'),maxNum=document.querySelector('.max-num'),guessBtn=document.getElementById('guess-btn'),guessInput=document.getElementById('guess-input'),message=document.querySelector('.message');minNum.textContent=min;maxNum.textContent=max;game.addEventListener('mousedown',function(e){if(e.target.className==='play-again'){window.location.reload()}});guessBtn.addEventListener('click',function(){let guess=parseInt(guessInput.value,10);if(isNaN(guess)||guess<min||guess>max){setMessage(`${min} から ${max} の数字を入力してください`,'red');return!1}
if(guess===winningNum){gameOver(!0,`おめでとうございます！！ 正解は... ${winningNum} でした。`)}else{guessesLeft-=1;if(guessesLeft===0){gameOver(!1,`あたなの負けです！ 正解は...  ${winningNum} でした。`)}else{guessInput.value='';guessInput.style.borderColor='red';compareNum(guess,winningNum)}}});function gameOver(won,msg){let color;won===!0?color='green':color='red';guessInput.disabled=!0;guessInput.style.borderColor=color;setMessage(msg,color);guessBtn.value='Play Again';guessBtn.className+='play-again'}
function getRandomNum(min,max){return Math.floor(Math.random()*(max-min+1)+min)}
function setMessage(msg,color){message.style.color=color;message.textContent=msg}
function compareNum(guess,winningNum){if(guess>winningNum){setMessage(`不正解！ あと${guessesLeft} 回挑戦できます（ヒント：もっと小さいよ！）`,'red')}else{setMessage(`不正解！ あと${guessesLeft} 回挑戦できます（ヒント：もっと大きいよ！）`,'red')}}